import React from "react";
import Piece from "./Pieces/Piece";
import BoardSquare from "./BoardSquare";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { ItemTypes } from "./Constants";

export default function Board({ knightPosition, posHandler }) {
    const squares = [];
    for (let i = 0; i < 64; i++) {
        squares.push(renderSquare(i, knightPosition, posHandler));
    }

    return (
        <DndProvider backend={HTML5Backend}>
            <div
                style={{
                    width: "100%",
                    height: "100%",
                    display: "flex",
                    flexWrap: "wrap",
                }}
            >
                {squares}
            </div>
        </DndProvider>
    );
}

function renderSquare(i, knightPosition, posHandler) {
    const x = i % 8;
    const y = Math.floor(i / 8);
    return (
        <div key={i} style={{ width: "12.5%", height: "12.5%" }}>
            {" "}
            {/* width: "12.5%", height: "12.5%" */}
            <BoardSquare x={x} y={y} posHandler={posHandler}>
                {renderPiece(x, y, knightPosition)}
            </BoardSquare>
        </div>
    );
}

function renderPiece(x, y, [knightX, knightY]) {
    if (x === knightX && y === knightY) {
        return (
            <Piece
                isWhite={true}
                imgSrc={"/Images/pieces/knight_w.png"}
                itemType={ItemTypes.KNIGHT}
            />
        );
    }
}
