import React from "react";
import "./RoomJoin.css";

const RoomJoin = () => {
    return (
        <div class="wrapper">
            <h1>Welcome to Chess Game</h1>
            <form method="POST">
                <div class="form-control">
                    <label for="room">Room id</label>
                    <input id="room" type="text" name="room_code" required />
                </div>
                <div class="form-control">
                    <label for="character_choice">Your colour</label>
                    <select for="character_choice" name="character_choice">
                        <option value="Black">Black</option>
                        <option value="White">White</option>
                    </select>
                </div>
                <input type="submit" class="button" value="Start Game" />
            </form>
        </div>
    );
};

export default RoomJoin;
