import styled from "styled-components";

export const GameContainer = styled.div`
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: #3a3b3c;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding-bottom: 120px;
`;

export const GamePlayer = styled.div`
    position: relative;
    width: 80vw;
    height: 80vw;
    max-width: 700px;
    max-height: 700px;
`;

export const Player = styled.div`
    min-height: 40px;
    color: white;
    font-weight: 500;
    font-size: 22px;
    padding-bottom: 20px;
    padding-left: 10px;

    &:nth-of-type(4) {
        padding-top: 10px;
    }
`;

export const PlayerName = styled.div`
    min-height: 37px;
`;

export const TakenPieces = styled.div`
    min-height: 28px;
    display: flex;
    width: 75%;
    & > img {
        width: 28px;
        height: 28px;
    }
`;

export const Buttons = styled.div`
    position: absolute;
    display: flex;
    flex-direction: column;
    right: 20px;
    top: 20px;
`;

export const RoomNumber = styled.div`
    text-align: center;
    color: white;
    font-size: 25px;
    font-weight: bold;
`;

export const Timer = styled.div`
    min-width: 100px;
    height: 60px;
    position: absolute;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 40px;
    color: white;
    right: 0;
    outline: 2px solid white;
    background-color: white;
`;
