import styled from "styled-components";

export const GameOverContainer = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-175px, -135px); // center container
    display: ${({ show }) => (show ? "flex" : "none")};
    width: 370px;
    height: 370px;
    background-color: white;
    z-index: 999;
`;
