import styled, { keyframes } from "styled-components";

export const GameOverContainer = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-185px, -145px); // center container
    display: ${({ show }) => (show ? "flex" : "none")};
    flex-direction: column;
    align-items: center;

    width: 370px;
    height: 370px;
    background-color: white;
    box-shadow: 0px 0px 5px black;
    border-radius: 5px;
    z-index: 999;

    & > h2 {
        font-size: 50px;
        color: limegreen;
        text-shadow: 0px 0px 2px black;
    }

    & > div {
        margin-top: -30px;
        font-size: 40px;
        color: black;
        font-weight: 700;
    }
`;

export const Buttons = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: space-around;
    padding-top: 70px;
`;

export const Button = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 80px;
    height: 45px;
    border: 2px solid black;
    font-size: 20px;
    font-weight: 700;
    border-radius: 5px;
`;

const bounce = keyframes`
 0%   { transform: scale(1,1) translateY(0); }
          10%  { transform: scale(1.1,.9) translateY(0); }
          30%  { transform: scale(.9,1.1)   translateY(-55px);}
          50%  { transform: scale(1.05,.95) translateY(0); }
          58%  { transform: scale(1,1) translateY(-7px); }
          65%  { transform: scale(1,1) translateY(0);}
          100% { transform: scale(1,1) translateY(0);}
`;

export const Winner = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;

    .w,
    .i,
    .n1,
    .n2,
    .e,
    .r {
        position: relative;
        color: #4cc9f0;
        text-shadow: 0 3px #4361ee, 0 5px #4361ee, 0 7px #4361ee;
    }

    .w {
        animation: ${bounce} 1s ease infinite;
        -webkit-animation: ${bounce} 1s ease infinite;
    }

    .i {
        animation: ${bounce} 1s ease infinite 0.1s;
        -webkit-animation: ${bounce} 1s ease infinite 0.1s;
    }

    .n1 {
        animation: ${bounce} 1s ease infinite 0.2s;
        -webkit-animation: ${bounce} 1s ease infinite 0.2s;
    }

    .n2 {
        animation: ${bounce} 1s ease infinite 0.3s;
        -webkit-animation: ${bounce} 1s ease infinite 0.3s;
    }

    .e {
        animation: ${bounce} 1s ease infinite 0.4s;
        -webkit-animation: ${bounce} 1s ease infinite 0.4s;
    }

    .r {
        animation: ${bounce} 1s ease infinite 0.5s;
        -webkit-animation: ${bounce} 1s ease infinite 0.5s;
    }
`;
