import React, { useState } from "react";
import useWebSocket, { ReadyState } from "react-use-websocket";
import { GameContainer, GamePlayer, GameWrap } from "./GameElements";
import Board from "../components/Board";

const Chess = () => {
    const [knightPosition, setPosition] = useState([0, 0]);

    const { readyState } = useWebSocket("ws://127.0.0.1:8000/", {
        onOpen: () => {
            console.log("Connected!");
        },
        onClose: () => {
            console.log("Disconnected!");
        },
    });

    const connectionStatus = {
        [ReadyState.CONNECTING]: "Connecting",
        [ReadyState.OPEN]: "Open",
        [ReadyState.CLOSING]: "Closing",
        [ReadyState.CLOSED]: "Closed",
        [ReadyState.UNINSTANTIATED]: "Uninstantiated",
    }[readyState];

    /* const [whitePieces, setWhitePosition] = useState({
    castle1_w:[0,0],
    knight1_w:[0,1],
    bishop1_w:[0,2],
    queen_w:[0,3],
    king_w:[0,4],
    castle2_w:[0,7],
    knight2_w:[0,6],
    bishop2_w:[0,5],
  })
  const [blackPieces, setBlackPosition] = useState({
    castle1_b:[0,0],
    knight1_b:[0,1],
    bishop1_b:[0,2],
    queen_b:[0,3],
    king_b:[0,4],
    castle2_b:[0,7],
    knight2_b:[0,6],
    bishop2_b:[0,5],
  }) */
    const changePosition = (x, y) => {
        setPosition([x, y]);
        /* setPosition(previousState => {
      return { ...previousState, piece: [x, y] }
    }); */
    };

    return (
        <GameContainer>
            <GamePlayer>
                <div>The WebSocket is currently {connectionStatus}</div>
                <Board
                    knightPosition={knightPosition}
                    posHandler={changePosition}
                />
                {/* <Board piecePositions={piecePositions} posHandler={changePosition}/> */}
            </GamePlayer>
        </GameContainer>
    );
};

export default Chess;

