import React, { memo, useState } from "react";
import { ItemTypes } from "../Constants";
import { useDrag } from "react-dnd";
import { PieceImg, PieceContainer } from "../ChessElements";

function Piece({ itemType, imgSrc, pieceName, piecePosition }) {
    const [isTurn, setTurn] = useState(true);
    console.log([pieceName, piecePosition]);
    var isDragging;
    useEffect(() => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: itemType,
            item: { pieceName, piecePosition },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
            canDrag: isTurn,
        }));
        isDragging = isDragging;
    }, []);

    return (
        <>
            {/* {console.log([pieceName, piecePosition])} */}
            <PieceContainer dragging={isDragging}>
                <PieceImg dragging={isDragging} src={imgSrc} ref={drag} />
            </PieceContainer>
        </>
    );
}

export default memo(Piece);
