import React, { useState } from "react";
import useWebSocket, { ReadyState } from "react-use-websocket";
import { GameContainer, GamePlayer, GameWrap } from "./GameElements";
import Board from "../components/Board";

const Chess = () => {
    const [knightPosition, setPosition] = useState([0, 0]);
    const [room, setRoom] = useState(1);

    const { readyState } = useWebSocket(
        "ws://127.0.0.1:8000/ws/" + room + "/",
        {
            onOpen: () => {
                console.log("Connected!");
            },
            onClose: () => {
                console.log("Disconnected!");
            },
            onMessage: (e) => {
                const dataFromServer = JSON.parse(e.data);
                console.log(dataFromServer);
            },
        }
    );

    const connectionStatus = {
        [ReadyState.CONNECTING]: "Connecting",
        [ReadyState.OPEN]: "Open",
        [ReadyState.CLOSING]: "Closing",
        [ReadyState.CLOSED]: "Closed",
        [ReadyState.UNINSTANTIATED]: "Uninstantiated",
    }[readyState];

    const [piecePositions, setPiecePosition] = useState({
        castle_w_1: { row: 0, col: 0 },
        knight_w_1: { row: 0, col: 1 },
        bishop_w_1: { row: 0, col: 2 },
        queen_w: { row: 0, col: 3 },
        king_w: { row: 0, col: 4 },
        castle_w_2: { row: 0, col: 7 },
        knight_w_2: { row: 0, col: 6 },
        bishop_w_2: { row: 0, col: 5 },

        castle_b_1: { row: 7, col: 0 },
        knight_b_1: { row: 7, col: 1 },
        bishop_b_1: { row: 7, col: 2 },
        queen_b: { row: 7, col: 3 },
        king_b: { row: 7, col: 4 },
        castle_b_2: { row: 7, col: 7 },
        knight_b_2: { row: 7, col: 6 },
        bishop_b_2: { row: 7, col: 5 },
    });

    const changePosition = (x, y) => {
        setPosition([x, y]);
        /* setPosition(previousState => {
      return { ...previousState, piece: [x, y] }
    }); */
    };

    return (
        <GameContainer>
            <GamePlayer>
                <div>The WebSocket is currently {connectionStatus}</div>
                <Board
                    knightPosition={knightPosition}
                    piecePositions={piecePositions}
                    posHandler={changePosition}
                />
                {/* <Board piecePositions={piecePositions} posHandler={changePosition}/> */}
            </GamePlayer>
        </GameContainer>
    );
};

export default Chess;

