let knightPosition = [0, 0];

/* export function moveKnight(toX, toY, posHandler, pieceName) {
  knightPosition = [toX, toY];
  posHandler(toX, toY);
} */

export function movePiece(toX, toY, posHandler, pieceName) {
    //knightPosition = [toX, toY];
    //posHandler(toX, toY);
    posHandler((prevState) => ({
        ...prevState,
        [pieceName]: { row: toY, col: toX },
    }));
}

export function canMoveKnight(toX, toY) {
    const [x, y] = knightPosition;
    const dx = toX - x;
    const dy = toY - y;

    return (
        (Math.abs(dx) === 2 && Math.abs(dy) === 1) ||
        (Math.abs(dx) === 1 && Math.abs(dy) === 2)
    );
}

export function canMovePiece(toX, toY, pieceName, piecePosition) {
    if (piecePosition == null) return false;
    const [x, y] = piecePosition;
    const dx = toX - x;
    const dy = toY - y;

    switch (pieceName) {
        case "knight":
            return (
                (Math.abs(dx) === 2 && Math.abs(dy) === 1) ||
                (Math.abs(dx) === 1 && Math.abs(dy) === 2)
            );

        case "castle":
            return (
                (Math.abs(dx) === 2 && Math.abs(dy) === 1) ||
                (Math.abs(dx) === 1 && Math.abs(dy) === 2)
            );

        default:
            break;
    }
}
