let knightPosition = [0, 0];

/* export function moveKnight(toX, toY, posHandler, pieceName) {
  knightPosition = [toX, toY];
  posHandler(toX, toY);
} */

export function canMoveKnight(toX, toY) {
    const [x, y] = knightPosition;
    const dx = toX - x;
    const dy = toY - y;

    return (
        (Math.abs(dx) === 2 && Math.abs(dy) === 1) ||
        (Math.abs(dx) === 1 && Math.abs(dy) === 2)
    );
}

export function movePiece(toX, toY, posHandler, item) {
    posHandler((prevState) => ({
        ...prevState,
        [item.pieceName]: { row: toY, col: toX },
    }));
}

export function canMovePiece(toX, toY, item) {
    if (!item.pieceName) return false;
    const dx = toX - item.piecePosition.col;
    const dy = toY - item.piecePosition.row;

    const pieceName = item.pieceName.split("_")[0];
    switch (pieceName) {
        case "castle":
            return (
                (Math.abs(dx) === 0 && Math.abs(dy) > 0) ||
                (Math.abs(dx) > 0 && Math.abs(dy) === 0)
            );

        case "knight":
            return (
                (Math.abs(dx) === 2 && Math.abs(dy) === 1) ||
                (Math.abs(dx) === 1 && Math.abs(dy) === 2)
            );

        case "bishop":
            return (
                !(Math.abs(dx) === 0 && Math.abs(dy) > 0) &&
                !(Math.abs(dx) > 0 && Math.abs(dy) === 0) &&
                (Math.abs(dx) === Math.abs(dy) || Math.abs(dx) === Math.abs(dy))
            );

        case "queen":
            return (
                (Math.abs(dx) === 0 && Math.abs(dy) > 0) ||
                (Math.abs(dx) > 0 && Math.abs(dy) === 0) ||
                Math.abs(dx) === Math.abs(dy) ||
                Math.abs(dx) === Math.abs(dy)
            );

        case "pawn":
            return Math.abs(dx) === 0 && Math.abs(dy) > 0;

        default:
            break;
    }
}
