import React, { useState } from "react";
import useWebSocket, { ReadyState } from "react-use-websocket";
import { useLocation } from "react-router-dom";
import { GameContainer, GamePlayer, Player, TakenPieces } from "./GameElements";
import Board from "../components/Board";
import { initBoardState } from "../components/BoardState";
import { useEffect } from "react";

const Chess = () => {
    //console.log(window.location.href.split("/")[4]);
    //const roomNumber = window.location.href.split("/")[4]
    //const playerName = window.location.href.split("/")[4]

    const location = useLocation();

    const [room, setRoom] = useState(location.state.room);
    const [name, setName] = useState(location.state.name);
    const [playerColour, setColour] = useState(location.state.colour);
    const [takenPieces, setTakenPieces] = useState({ white: [], black: [] });

    const boardRotation = getBoardRotation(playerColour);
    const isWhite = playerColour === "white";
    // Sets initial piece positions ensuring that players colour is always at bottom
    const [boardState, setBoardState] = useState(
        initBoardState(isWhite, boardRotation)
    );
    const [prevBoardState, setPrevBoardState] = useState(null);

    const { sendMessage, readyState } = useWebSocket(
        "ws://127.0.0.1:8000/ws/" + room + "/",
        {
            onOpen: () => {
                console.log("Connected!");
            },
            onClose: () => {
                console.log("Disconnected!");
            },
            onMessage: (e) => {
                const dataFromServer = JSON.parse(e.data);
                console.log("receive");
                if (
                    dataFromServer.payload.event === "MOVE" &&
                    dataFromServer.payload.message.sender !== playerColour
                ) {
                    setBoardState(
                        flipBoard(dataFromServer.payload.message.boardState)
                    );
                }
            },
        }
    );

    const connectionStatus = {
        [ReadyState.CONNECTING]: "Connecting",
        [ReadyState.OPEN]: "Open",
        [ReadyState.CLOSING]: "Closing",
        [ReadyState.CLOSED]: "Closed",
        [ReadyState.UNINSTANTIATED]: "Uninstantiated",
    }[readyState];

    useEffect(() => {
        // Send the current board state to the websocket group
        // Only send message if the board state has changed to avoid infintite loop of sending
        console.log(false);
        if (JSON.stringify(prevBoardState) !== JSON.stringify(boardState)) {
            console.log(true);
            setPrevBoardState(boardState);
            sendMessage(
                JSON.stringify({
                    type: "send_message",
                    message: { boardState, sender: playerColour },
                    event: "MOVE",
                })
            );
        }
    }, [boardState]);

    const opponentColour = playerColour === "white" ? "black" : "white";

    return (
        <GameContainer>
            <GamePlayer>
                <div style={{ color: "white" }}>
                    The WebSocket is currently {connectionStatus}
                </div>
                <Player>
                    <div>{playerColour}</div>
                    <TakenPieces>
                        {takenPieces[playerColour].map((piecePath, i) => {
                            return <img key={i} src={piecePath} />;
                        })}
                    </TakenPieces>
                </Player>
                <Board
                    boardState={boardState}
                    setBoardState={setBoardState}
                    setTakenPieces={setTakenPieces}
                />
                <Player>
                    <div>{playerColour}</div>
                    <TakenPieces>
                        {takenPieces[opponentColour].map((piecePath, i) => {
                            return <img key={i} src={piecePath} />;
                        })}
                    </TakenPieces>
                </Player>
            </GamePlayer>
        </GameContainer>
    );
};

export default Chess;

const getBoardRotation = (colour) => {
    if (colour === "white") return { white: [7, 6], black: [0, 1] };
    else if (colour === "black") return { white: [0, 1], black: [7, 6] };
    throw "Colour must be black or white";
};

const flipBoard = (boardState) => {
    for (const [_, state] of Object.entries(boardState)) {
        state.row = 7 - state.row;
        state.isOwner = !state.isOwner;
    }
    return boardState;
};

