import styled from "styled-components";

export const GameContainer = styled.div`
    position: fixed;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #3a3b3c;
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 999;
`;

export const GamePlayer = styled.div`
    width: 80vw;
    height: 80vw;
    max-width: 700px;
    max-height: 700px;
    margin-top: 30px;
`;
