function Piece({ isWhite, itemType, imgSrc }) {
    const [isTurn, setTurn] = useState(true);

    const [{ isDragging }, drag] = useDrag(() => ({
        type: itemType,
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
        canDrag: isTurn,
    }));

    //const imageFile = '/Images/pieces/knight_w.png';

    return (
        <>
            <PieceContainer dragging={isDragging}>
                <PieceImg dragging={isDragging} src={imgSrc} ref={drag} />
            </PieceContainer>
        </>
    );
}

export default memo(Piece);
