import styled from "styled-components";

export const GameContainer = styled.div`
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: #3a3b3c;
    display: flex;
    justify-content: center;
    align-items: center;
    padding-bottom: 120px;
`;

export const GamePlayer = styled.div`
    width: 80vw;
    height: 80vw;
    max-width: 700px;
    max-height: 700px;
`;

export const Player = styled.div`
    color: white;
    font-weight: 500;
    font-size: 22px;
    padding-bottom: 20px;
    padding-left: 20px;

    &:nth-of-type(3) {
        padding-top: 5px;
    }
`;

export const TakenPieces = styled.div`
    height: 20px;

    & > img {
        width: 28px;
        height: 28px;
    }
`;
