    toX,
    toY,
    setBoardState,
    setTakenPieces,
    item,
    boardState
) {
    const square = squareIsEmpty(toX, toY, boardState);
    if (square != null) {
        if (!square.isOwner) {
            boardState[square.piece].isAlive = false;

            // Track what pieces are taken so that they can be rendered under player name
            const pieceNameSplit = square.piece.split("_");
            const piecePathName = pieceNameSplit[0] + "_" + pieceNameSplit[1];
