import React, { memo, useState } from "react";
import { ItemTypes } from "../Constants";
import { useDrag } from "react-dnd";
import { PieceImg, PieceContainer } from "../ChessElements";

function Piece({ itemType, imgSrc, pieceName, pieceState, playerColour }) {
    const [isTurn, setTurn] = useState(true);
    const [{ isDragging }, drag] = useDrag(() => ({
        type: itemType,
        item: { pieceName, pieceState },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
        canDrag: pieceState.isOwner,
    }));

    return (
        <>
            <PieceContainer dragging={isDragging}>
                <PieceImg
                    owned={pieceState.isOwner}
                    dragging={isDragging}
                    src={imgSrc}
                    ref={drag}
                />
            </PieceContainer>
        </>
    );
}

export default memo(Piece);
