import React, { memo, useState } from "react";
import { Square } from "./ChessElements";
import { ItemTypes } from "./Constants";
import { useDrop } from "react-dnd";
import { canMovePiece, movePiece } from "./Game";
import { PossibleMoveDot, MoveHoverHighlight } from "./ChessElements";
import { useEffect } from "react";

function BoardSquare({ x, y, children, posHandler, boardState }) {
    const [knightPosition, setKnightPosition] = useState(1);
    const dark = (x + y) % 2 === 1;
    const [{ isOver, canDrop }, drop] = useDrop(
        () => ({
            accept: [ItemTypes.PIECE],
            canDrop: (item) => canMovePiece(x, y, item, boardState),
            drop: (item) =>
                movePiece(
                    x,
                    y,
                    posHandler,
                    item,
                    boardState,
                    setKnightPosition
                ),
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
                canDrop: !!monitor.canDrop(),
            }),
        }),
        [x, y]
    );

    useEffect(() => {
        console.log(knightPosition);
    }, [knightPosition]);

    return (
        <div
            ref={drop}
            style={{
                position: "relative",
                width: "100%",
                height: "100%",
            }}
        >
            <Square dark={dark}>{children}</Square>

            {!isOver && canDrop && <PossibleMoveDot dark={dark} />}
            {isOver && canDrop && <MoveHoverHighlight />}
        </div>
    );
}

export default memo(BoardSquare);
