import React, { useState } from "react";
import "./RoomJoin.css";
import { useNavigate } from "react-router-dom";

const RoomJoin = () => {
    const [room, setRoom] = useState(1);
    const [name, setName] = useState(null);
    const [colour, setColour] = useState("black");

    const navigate = useNavigate();

    return (
        <div className="wrapper">
            <h1>Welcome to Chess Game</h1>
            <form
                onSubmit={() =>
                    navigate("/play/" + room + "/?&colour=" + colour, {
                        state: {
                            room: room,
                            colour: colour,
                            name: name,
                        },
                    })
                }
            >
                <div className="form-control">
                    <label htmlFor="room">Room id</label>
                    <input
                        id="room"
                        type="text"
                        name="room_code"
                        required
                        onChange={(e) => {
                            setRoom(e.target.value);
                        }}
                    />
                </div>
                <div className="form-control">
                    <label htmlFor="name">Name</label>
                    <input
                        id="name"
                        type="text"
                        name="player_name"
                        required
                        onChange={(e) => {
                            setName(e.target.value);
                        }}
                    />
                </div>
                <div className="form-control">
                    <label htmlFor="character_choice">Your colour</label>
                    <select
                        htmlFor="character_choice"
                        name="character_choice"
                        onChange={(e) => {
                            setColour(e.target.value);
                        }}
                    >
                        <option value="black">Black</option>
                        <option value="white">White</option>
                    </select>
                </div>
                <input type="submit" className="button" value="Start Game" />
                {/* <input
                    type="hidden"
                    name="csrfmiddlewaretoken"
                    value={getCookie("csrftoken")}
                /> */}
            </form>
        </div>
    );
};

export default RoomJoin;

function getCookie(name) {
    var cookieValue = null;
    if (document.cookie && document.cookie !== "") {
        var cookies = document.cookie.split(";");
        for (var i = 0; i < cookies.length; i++) {
            var cookie = cookies[i].trim(); //$.trim(cookies[i]);
            if (cookie.substring(0, name.length + 1) === name + "=") {
                cookieValue = decodeURIComponent(
                    cookie.substring(name.length + 1)
                );
                break;
            }
        }
    }
    return cookieValue;
}
