export function movePiece(toX, toY, setBoardState, item, boardState) {
    const square = squareIsEmpty(toX, toY, boardState);
    if (square != null) {
        if (!square.isOwner) {
            boardState[square.piece].isAlive = false;
        }
    }

    //console.log(boardState[item.pieceName].row);

    setBoardState((prevState) => ({
        ...prevState,
        [item.pieceName]: {
            row: toY,
            col: toX,
            hasMoved: true,
            isOwner: true,
            isAlive: true,
        },
    }));
    //console.log(boardState[item.pieceName].row);
}

export function canMovePiece(toX, toY, item, boardState, monitor) {
    //console.log(monitor.getItem().pieceState.row);
    const square = squareIsEmpty(toX, toY, boardState);
    //toX === 0 && toY === 6 ? console.log(square) : console.log(null);
    //console.log(square);
    if (square && square.state.isOwner) return false;

    const dx = toX - item.pieceState.col;
    const dy = toY - item.pieceState.row;

    const pieceName = item.pieceName.split("_")[0];

    const diagCollisions = checkDiagCollisions(toX, toY, boardState, monitor);
    const straightCollisions = checkStraightCollisions(
        toX,
        toY,
        boardState,
        monitor
    );
    //console.log([diagCollisions, toX, toY]);

    switch (pieceName) {
        case "castle":
            return (
                (Math.abs(dx) === 0 && Math.abs(dy) > 0) ||
                (Math.abs(dx) > 0 && Math.abs(dy) === 0)
            );

        case "knight":
            return (
                (Math.abs(dx) === 2 && Math.abs(dy) === 1) ||
                (Math.abs(dx) === 1 && Math.abs(dy) === 2)
            );

        case "bishop":
            return (
                !(Math.abs(dx) === 0 && Math.abs(dy) > 0) &&
                !(Math.abs(dx) > 0 && Math.abs(dy) === 0) &&
                (Math.abs(dx) === Math.abs(dy) || Math.abs(dx) === Math.abs(dy))
            );

        case "queen":
            return (
                (((Math.abs(dx) === 0 && Math.abs(dy) > 0) ||
                    (Math.abs(dx) > 0 && Math.abs(dy) === 0)) &&
                    !straightCollisions) ||
                ((Math.abs(dx) === Math.abs(dy) ||
                    Math.abs(dx) === Math.abs(dy)) &&
                    !diagCollisions)
            );

        case "king":
            return (
                (Math.abs(dx) === 1 || Math.abs(dx) === 0) &&
                (Math.abs(dy) === 1 || Math.abs(dy) === 0)
            );

        case "pawn":
            if (!item.pieceState.hasMoved)
                // Negative direction moves up the board (player always at bottom)
                return Math.abs(dx) === 0 && (dy === -1 || dy === -2);
            return dx === 0 && dy === -1;

        default:
            break;
    }
}

function squareIsEmpty(x, y, boardState) {
    for (const [piece, state] of Object.entries(boardState)) {
        if (x === state.col && y === state.row) {
            return { piece, state };
        }
    }
    return null;
}

/**
 * Checks if there is a piece in the diagonal of the selected piece and the current square
 * @param {number} x x coordinate of the square currently being checked
 * @param {number} y y coordinate of the square currently being checked
 * @param {Object} boardState current state of the board
 * @param {Object} monitor contains data about the currently dragged piece
 * @returns true if an opponents piece is inbetween the square and the currently dragged piece, false otherwise
 */
function checkDiagCollisions(x, y, boardState, monitor) {
    const row = monitor.getItem().pieceState.row;
    const col = monitor.getItem().pieceState.col;

    const dirX = Math.sign(x - col);
    const dirY = Math.sign(y - row);
    for (let i = 1; i < Math.abs(x - col) + 1; i++) {
        for (const [_, state] of Object.entries(boardState)) {
            if (
                col + i * dirX === state.col &&
                row + i * dirY === state.row &&
                state.isAlive
            ) {
                if (i !== Math.abs(x - col)) {
                    // Stops opponents pieces from being taken when hidden behind each other
                    return true;
                }
                return state.isOwner;
            }
        }
    }
    return false;
}

function checkStraightCollisions(x, y, boardState, monitor) {
    const row = monitor.getItem().pieceState.row;
    const col = monitor.getItem().pieceState.col;
    const dx = Math.abs(x - col);
    const dy = Math.abs(y - row);

    if (dx > 0 && dy > 0) return true;
    const dirX = Math.sign(x - col);
    const dirY = Math.sign(y - row);
    //console.log([dirX, dirY]);

    for (let i = 1; i < Math.max(dx, dy) + 1; i++) {
        for (const [_, state] of Object.entries(boardState)) {
            if (state.isAlive) {
                if (dy === 0) {
                    //console.log(_);
                    //console.log([dirX, dirY]);
                    if (col + i * dirX === state.col) {
                        console.log([dirX, dirY]);
                        if (i !== dx) {
                            // Stops opponents pieces from being taken when hidden behind each other
                            return true;
                        }
                        return state.isOwner;
                    }
                } else {
                    if (row + i * dirY === state.row) {
                        if (i !== dy) {
                            // Stops opponents pieces from being taken when hidden behind each other
                            return true;
                        }
                        return state.isOwner;
                    }
                }
            }
            /* if (state.isAlive) {
                if (col + i * dirX === state.col) {
                    console.log(true);
                    if (i !== Math.abs(x - col)) {
                        // Stops opponents pieces from being taken when hidden behind each other
