import React, { useState } from "react";
import Piece from "./Pieces/Piece";
import BoardSquare from "./BoardSquare";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { ItemTypes } from "./Constants";

export default function Board({ boardState, posHandler }) {
    const [knightPosition, setKnightPosition] = useState(1);
    const [dboardState, setBoardState] = useState(boardState);

    const squares = [];
    for (let i = 0; i < 64; i++) {
        squares.push(
            renderSquare(i, boardState, posHandler, setKnightPosition)
        );
    }

    return (
        <DndProvider backend={HTML5Backend}>
            <div
                style={{
                    width: "100%",
                    height: "100%",
                    display: "flex",
                    flexWrap: "wrap",
                }}
            >
                {squares}
            </div>
        </DndProvider>
    );
}

function renderSquare(i, boardState, posHandler, setKnightPosition) {
    const x = i % 8;
    const y = Math.floor(i / 8);
    let pieceImgPath, pieceName, pieceState;

    for (const [piece, state] of Object.entries(boardState)) {
        if (x === state.col && y === state.row) {
            const pieceNameSplit = piece.split("_");
            pieceName = piece;
            pieceState = state;
            const piecePathName = pieceNameSplit[0] + "_" + pieceNameSplit[1];
            pieceImgPath = "/Images/pieces/" + piecePathName + ".png";
            break;
        }
    }
    return (
        <div key={i} style={{ width: "12.5%", height: "12.5%" }}>
            <BoardSquare
                x={x}
                y={y}
                posHandler={posHandler}
                boardState={boardState}
                setKnightPosition={setKnightPosition}
            >
                {/* {pieceName === "queen_b"
                    ? console.log([
                          pieceName,
                          boardState["queen_b"],
                          pieceState.isAlive,
                      ])
                    : null} */}
                {/* {pieceName === "queen_b"
                    ? console.log([
                          pieceName,
                          boardState["queen_b"],
                          pieceState.isAlive,
                      ])
                    : null} */}
                {/* {pieceName === "queen_w"
                    ? console.log([
                          pieceName,
                          boardState["queen_w"],
                          pieceState.isAlive,
                      ])
                    : null} */}
                {/* {pieceName === "queen_w" ? console.log(boardState) : null} */}
                {/* {console.log([pieceName, boardState["queen_b"]])} */}
                {/* {console.log([pieceName, boardState["queen_w"]])} */}
                {pieceName && (
                    <Piece
                        imgSrc={pieceImgPath}
                        itemType={ItemTypes.PIECE}
                        pieceName={pieceName}
                        pieceState={{ ...pieceState }}
                    />
                )}
            </BoardSquare>
        </div>
    );
}
