import React, { useState } from "react";
import useWebSocket, { ReadyState } from "react-use-websocket";
import { useLocation } from "react-router-dom";
import { GameContainer, GamePlayer, Player, TakenPieces } from "./GameElements";
import Board from "../components/Board";
import { useEffect } from "react";
import { useRef } from "react";
import { checkIfOpponentInCheck } from "../components/Game";

const Chess = () => {
    const location = useLocation();

    const [room, setRoom] = useState(location.state.room);
    const [name, setName] = useState(location.state.name);
    const [opponentName, setOpponentName] = useState("charlie");
    const [playerColour, setColour] = useState(location.state.colour);
    const [takenPieces, setTakenPieces] = useState({
        white: [],
        black: [
            require("../assets/images/pieces/pawn_b.png"),
            require("../assets/images/pieces/queen_b.png"),
            require("../assets/images/pieces/bishop_b.png"),
            require("../assets/images/pieces/king_b.png"),
        ],
    });

    const boardRotation = getBoardRotation(playerColour);
    const isWhite = playerColour === "white";
    // Sets initial piece positions ensuring that players colour is always at bottom
    const [boardState, setBoardState] = useState(
        initBoardState(isWhite, boardRotation)
    );
    const [prevBoardState, setPrevBoardState] = useState(null);

    const [audio] = useState({
        check: new Audio(require("../assets/sounds/check.mp3")),
    });

    const { sendMessage, readyState } = useWebSocket(
        "ws://127.0.0.1:8000/ws/" + room + "/",
        {
            onOpen: () => {
                console.log("Connected!");
                sendMessage(
                    JSON.stringify({
                        type: "send_message",
                        message: { opponentName: name },
                        event: "START",
                    })
                );
            },
            onClose: () => {
                console.log("Disconnected!");
            },
            onMessage: (e) => {
                const dataFromServer = JSON.parse(e.data);
                switch (dataFromServer.payload.event) {
                    case "START":
                        if (
                            opponentName === null &&
                            dataFromServer.payload.message.opponentName !== name
                        ) {
                            //
                            console.log(
                                dataFromServer.payload.message.opponentName
                            );
                            setOpponentName(
                                dataFromServer.payload.message.opponentName
                            );
                            sendMessage(
                                JSON.stringify({
                                    type: "send_message",
                                    message: { opponentName: name },
                                    event: "START",
                                })
                            );
                        }

                    case "MOVE":
                        if (
                            dataFromServer.payload.message.opponent.colour !==
                            playerColour
                        ) {
                            setBoardState(
                                flipBoard(
                                    dataFromServer.payload.message.boardState
                                )
                            );

                            setTakenPieces(
                                dataFromServer.payload.message.takenPieces
                            );
                            setOpponentName(
                                dataFromServer.payload.message.opponent.name
                            );
                        }
                }
            },
        }
    );

    const connectionStatus = {
        [ReadyState.CONNECTING]: "Connecting",
        [ReadyState.OPEN]: "Open",
        [ReadyState.CLOSING]: "Closing",
        [ReadyState.CLOSED]: "Closed",
        [ReadyState.UNINSTANTIATED]: "Uninstantiated",
    }[readyState];

    useEffect(() => {
        // Send the current board state to the websocket group
        // Only send message if the board state has changed to avoid infintite loop of sending
        //console.log(false);
        if (JSON.stringify(prevBoardState) !== JSON.stringify(boardState)) {
            //console.log(true);
            setPrevBoardState(boardState);
            sendMessage(
                JSON.stringify({
                    type: "send_message",
                    message: {
                        boardState,
                        takenPieces,
                        opponent: { name: name, colour: playerColour },
                    },
                    event: "MOVE",
                })
            );

            const opponentKing = boardState["king_w"].isOwner
                ? boardState["king_b"]
                : boardState["king_w"];
            if (checkIfOpponentInCheck(boardState, opponentKing)) {
                console.log(true);
                audio.check.play();
            }
            //audio.move.play();
        }
    }, [boardState]);

    const opponentColour = playerColour === "white" ? "black" : "white";

    return (
        <GameContainer>
            <GamePlayer>
                {/* <div style={{ color: "white" }}>
                    The WebSocket is currently {connectionStatus}
                </div> */}
                <Player>
                    <div>{opponentName}</div>
                    <TakenPieces>
                        {takenPieces[playerColour].map((piecePath, i) => {
                            return <img key={i} src={piecePath} />;
                        })}
                    </TakenPieces>
                </Player>
                <Board
                    boardState={boardState}
                    setBoardState={setBoardState}
                    setTakenPieces={setTakenPieces}
                />
                <Player>
                    <div>{name}</div>
                    <TakenPieces>
                        {takenPieces[opponentColour].map((piecePath, i) => {
                            return <img key={i} src={piecePath} />;
                        })}
                    </TakenPieces>
                </Player>
            </GamePlayer>
        </GameContainer>
    );
};

export default Chess;

const getBoardRotation = (colour) => {
    if (colour === "white") return { white: [7, 6], black: [0, 1] };
    else if (colour === "black") return { white: [0, 1], black: [7, 6] };
    throw "Colour must be black or white";
};

const flipBoard = (boardState) => {
    for (const [_, state] of Object.entries(boardState)) {
        state.row = 7 - state.row;
        state.col = 7 - state.col;
        state.isOwner = !state.isOwner;
    }
    return boardState;
};

export const initBoardState = (isWhite, boardRotation) => {
    const pieceNames = [
        "castle_w_1",
        "knight_w_1",
        "bishop_w_1",
        "queen_w",
        "king_w",
        "bishop_w_2",
        "knight_w_2",
        "castle_w_2",
        "pawn_w_1",
        "pawn_w_2",
        "pawn_w_3",
        "pawn_w_4",
        "pawn_w_5",
        "pawn_w_6",
        "pawn_w_7",
        "pawn_w_8",
        "castle_b_1",
        "knight_b_1",
        "bishop_b_1",
        "queen_b",
        "king_b",
        "bishop_b_2",
        "knight_b_2",
        "castle_b_2",
        "pawn_b_1",
        "pawn_b_2",
        "pawn_b_3",
        "pawn_b_4",
        "pawn_b_5",
        "pawn_b_6",
        "pawn_b_7",
        "pawn_b_8",
    ];
    const boardState = {};
    for (let i = 0; i < 32; i++) {
        // First 16 elements in  pieces array are white rest are black
        // Each 8 elements in array is a row
        if (i < 16) {
            let row;
            if (i < 8) row = boardRotation.white[0];
            else row = boardRotation.white[1];
            boardState[pieceNames[i]] = {
                row: row,
                col: i % 8,
                hasMoved: false,
                isOwner: isWhite,
                isAlive: true,
            };
        } else {
            let row;
            if (i < 24) row = boardRotation.black[0];
            else row = boardRotation.black[1];
            boardState[pieceNames[i]] = {
                row: row,
                col: i % 8,
                hasMoved: false,
                isOwner: !isWhite,
                isAlive: true,
            };
        }
    }
    return boardState;
};

