 * @returns true if the dragged piece can be moved to the square at (x,y), false otherwise
 */
export function canMovePiece(toX, toY, item, boardState, draggedPiece) {
    const square = squareIsEmpty(toX, toY, boardState);

    // if a friendly piece is in a square, don't allow this piece to be moved there
    if (square && square.state.isOwner && square.state.isAlive) return false;

    // calculate the difference between the new square and the piece being dragged
    var dx = Math.abs(toX - item.pieceState.col);
    var dy = Math.abs(toY - item.pieceState.row);

    const pieceName = item.pieceName.split("_")[0];

    // Check the row and column of the piece to see if the movement should be blocked
    // by another piece
    const straightCollision = checkStraightCollisions(
        toX,
        toY,
        boardState,
        draggedPiece
    );
    // Check the diagonal of the piece to see if the movement should be blocked
    // by another piece
    const diagCollision = checkDiagCollisions(
        toX,
        toY,
        boardState,
        draggedPiece
    );

    if (!boardState["pawn_b_1"].hasMoved) {
        boardState["queen_w"].row = 4;
        boardState["queen_w"].col = 7;

        boardState["pawn_w_1"].row = 4;
        boardState["pawn_w_1"].col = 5;

        boardState["pawn_b_1"].row = 5;
        boardState["pawn_b_1"].col = 6;
    }

    // Check if the piece is currently under attack
    const isPieceUnderAttack = checkIfMoveUnderAttack(
        item.pieceState.col,
        item.pieceState.row,
        boardState
    );

    let pieceAttackingKing, isProtectingKing;
    if (pieceName !== "king") {
        // Check if the piece is defending the king
        // Get the piece that is attacking the king if true
        pieceAttackingKing = checkIfPieceIsProtectingKing(
            item.pieceState.col,
            item.pieceState.row,
            boardState,
            draggedPiece.pieceName
