            }
            // Allow pawn to take opponents pieces diagonal by 1 square
            if ((dx === 1 || dx === -1) && dy === -1 && square !== null)
                return true;

            // Allow pawn to move forward by 1 square
            return dx === 0 && dy === -1 && square === null;

        default:
            return false;
    }
}

