    king_b:[0,4],
    castle2_b:[0,7],
    knight2_b:[0,6],
    bishop2_b:[0,5],
  }) */
  const changePosition = (x, y) => {
    setPosition([x, y]);
    /* setPosition(previousState => {
      return { ...previousState, piece: [x, y] }
    }); */
  }
  
  return (
    <GameContainer>
      <GamePlayer>
        <Board knightPosition={knightPosition} posHandler={changePosition}/>
        {/* <Board piecePositions={piecePositions} posHandler={changePosition}/> */}
      </GamePlayer>
    </GameContainer>
  )
}

export default Chess
