export function movePiece(toX, toY, posHandler, item, boardState) {
    const square = squareIsEmpty(toX, toY, boardState);
    if (square != null) {
        if (!square.isOwner) {
            square.state.isAlive = false;
        }
    }

    posHandler((prevState) => ({
        ...prevState,
        [item.pieceName]: {
            row: toY,
            col: toX,
            hasMoved: true,
            isOwner: true,
            isAlive: true,
        },
    }));
}

export function canMovePiece(toX, toY, item, boardState) {
    const dx = toX - item.pieceState.col;
    const dy = toY - item.pieceState.row;
    console.log("draggong");

    const pieceName = item.pieceName.split("_")[0];

    switch (pieceName) {
        case "castle":
            return (
                (Math.abs(dx) === 0 && Math.abs(dy) > 0) ||
                (Math.abs(dx) > 0 && Math.abs(dy) === 0)
            );

        case "knight":
            return (
                (Math.abs(dx) === 2 && Math.abs(dy) === 1) ||
                (Math.abs(dx) === 1 && Math.abs(dy) === 2)
            );

        case "bishop":
            return (
                !(Math.abs(dx) === 0 && Math.abs(dy) > 0) &&
                !(Math.abs(dx) > 0 && Math.abs(dy) === 0) &&
                (Math.abs(dx) === Math.abs(dy) || Math.abs(dx) === Math.abs(dy))
            );

        case "queen":
            return (
                (Math.abs(dx) === 0 && Math.abs(dy) > 0) ||
                (Math.abs(dx) > 0 && Math.abs(dy) === 0) ||
                Math.abs(dx) === Math.abs(dy) ||
                Math.abs(dx) === Math.abs(dy)
            );

        case "king":
            return (
                (Math.abs(dx) === 1 || Math.abs(dx) === 0) &&
                (Math.abs(dy) === 1 || Math.abs(dy) === 0)
            );

        case "pawn":
            if (!item.pieceState.hasMoved)
                // Negative direction moves up the board (player always at bottom)
                return Math.abs(dx) === 0 && (dy === -1 || dy === -2);
            return dx === 0 && dy === -1;

        default:
            break;
    }
}

function squareIsEmpty(x, y, boardState) {
    for (const [piece, state] of Object.entries(boardState)) {
        if (x === state.col && y === state.row) {
            return { piece, state };
        }
    }
    return null;
}
