import React, { useState } from "react";
import useWebSocket, { ReadyState } from "react-use-websocket";
import { GameContainer, GamePlayer } from "./GameElements";
import Board from "../components/Board";
import { initBoardState } from "../components/BoardState";

const Chess = () => {
    const [room, setRoom] = useState(1);
    const [playerColour, setColour] = useState("white");

    const { readyState } = useWebSocket(
        "ws://127.0.0.1:8000/ws/" + room + "/",
        {
            onOpen: () => {
                console.log("Connected!");
            },
            onClose: () => {
                console.log("Disconnected!");
            },
            onMessage: (e) => {
                const dataFromServer = JSON.parse(e.data);
                console.log(dataFromServer);
            },
        }
    );

    const connectionStatus = {
        [ReadyState.CONNECTING]: "Connecting",
        [ReadyState.OPEN]: "Open",
        [ReadyState.CLOSING]: "Closing",
        [ReadyState.CLOSED]: "Closed",
        [ReadyState.UNINSTANTIATED]: "Uninstantiated",
    }[readyState];

    const getBoardRotation = (colour) => {
        if (colour === "white") return { white: [7, 6], black: [0, 1] };
        else if (colour === "black") return { white: [0, 1], black: [7, 6] };
        throw "Colour must be black or white";
    };

    const boardRotation = getBoardRotation(playerColour);
    const isWhite = playerColour === "white";

    // Sets initial piece positions ensuring that players colour is always at bottom
    const [boardState, setBoardState] = useState(
        initBoardState(isWhite, boardRotation)
    );

    return (
        <GameContainer>
            <PlayerScore></PlayerScore>
            <GamePlayer>
                <div>The WebSocket is currently {connectionStatus}</div>
                <Board boardState={boardState} setBoardState={setBoardState} />
            </GamePlayer>
            <PlayerScore></PlayerScore>
        </GameContainer>
    );
};

export default Chess;

