export function canMovePiece(toX, toY, item, boardState, monitor) {
    const square = squareIsEmpty(toX, toY, boardState);

    if (square && square.state.isOwner && square.state.isAlive) return false;

    var dx = Math.abs(toX - item.pieceState.col);
    var dy = Math.abs(toY - item.pieceState.row);

    const pieceName = item.pieceName.split("_")[0];

    switch (pieceName) {
        case "castle":
            var straightCollisions = checkStraightCollisions(
                toX,
                toY,
                boardState,
                monitor
            );
            return (
                ((dx === 0 && dy > 0) || (dx > 0 && dy === 0)) &&
                !straightCollisions
            );

        case "knight":
            return (dx === 2 && dy === 1) || (dx === 1 && dy === 2);

        case "bishop":
            var diagCollisions = checkDiagCollisions(
                toX,
                toY,
                boardState,
                monitor
            );
            return (
                !(dx === 0 && dy > 0) &&
                !(dx > 0 && dy === 0) &&
                (dx === dy || dx === dy) &&
                !diagCollisions
            );

        case "queen":
            var straightCollisions = checkStraightCollisions(
                toX,
                toY,
                boardState,
                monitor
            );
            var diagCollisions = checkDiagCollisions(
                toX,
                toY,
                boardState,
                monitor
            );
            return (
                (((dx === 0 && dy > 0) || (dx > 0 && dy === 0)) &&
                    !straightCollisions) ||
                ((dx === dy || dx === dy) && !diagCollisions)
            );

        case "king":
            const moveNotInCheck = checkIfMoveInCheck();
            return (dx === 1 || dx === 0) && (dy === 1 || dy === 0);

        case "pawn":
            var straightCollisions = checkStraightCollisions(
                toX,
                toY,
                boardState,
                monitor
            );
            // Recalculate dx/dy with direction
            dx = toX - item.pieceState.col;
            dy = toY - item.pieceState.row;
            // Allow pawn to move 2 squares on first move
            if (!item.pieceState.hasMoved) {
                // Negative direction moves up the board (player always at bottom)
                if (
                    dx === 0 &&
                    (dy === -1 || dy === -2) &&
                    !straightCollisions
                ) {
                    return square === null;
                }
            }
            // Allow pawn to take opponents pieces diagonal by 1 square
            if ((dx === 1 || dx === -1) && dy === -1 && square !== null)
                return true;

            // Allow pawn to move forward by 1 square
            return dx === 0 && dy === -1 && square === null;

        default:
            return false;
    }
}

